import React, {Component} from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';

const container = {
  position: 'absolute', left: '50%', top: '50%',
  transform: 'translate(-50%, -50%)'
}


const card = {
    Width: 500,
    height: 500,
    position: 'absolute', left: '50%', top: '50%',
    transform: 'translate(-50%, -50%)'
};
const title = {
    fontSize: 14,
};
const subtitle = {
    marginBottom: 50,
};
const emailForm = {
  width: 400,
}
const passwordForm = {
  width: 400,
};

const button = {
  width: 300,
  height: 60,
  borderRadius: 30,
  marginTop: 50
}


export default class Login extends Component {

  constructor(){
    super();
    this.state = {
      email: '' ,
      password: ''
    }
    this.setEmail.bind(this)
    this.setPassword.bind(this)
  }

  setEmail(email){
    this.setState({email: email})
    console.log(this.state.email)

  }
  setPassword(password){
    this.setState({password: password})
  }



  render(){
    return (
      <Card style={card}>
        <CardContent>
          <Typography style={title} color="textSecondary" gutterBottom>
          </Typography>
          <Typography variant="h5" component="h2">
            Sign In
          </Typography>
          <Typography style={subtitle} color="textSecondary">
            to your new addiction
          </Typography>
              <form className={emailForm} noValidate autoComplete="off">
                <TextField style={{width: 300, marginBottom: 50}}  id="standard-basic" label="email" color="secondary" onChange={(email)=> this.setEmail(email)}/>
              </form>
              <form className={passwordForm} noValidate autoComplete="off">
                <TextField style={{width: 300}}  id="standard-basic" label="password" color="secondary" onChange={(password) => this.setPassword(password)}/>
              </form>
              <Button style={button} variant="contained" color="secondary">
                Log in
              </Button>
              <Typography style={{marginTop:20}} color="textSecondary">
                Don't have an account?
              </Typography>


        </CardContent>
      </Card>
    );
  }
}
